/**
 * Bearer authentication method.
 *
 * @since 0.8
 */
private static final class Bearer implements Method {
    @Override
    public Slice slice(final String action) {
        return new DockerSlice(new AstoDocker(new InMemoryStorage()), new Permissions.Single(TestAuthentication.ALICE.name(), action), new BearerAuthScheme(token -> CompletableFuture.completedFuture(Stream.of(TestAuthentication.ALICE, TestAuthentication.BOB).filter(user -> token.equals(token(user))).map(user -> new Authentication.User(user.name())).findFirst()), ""));
    }
}

